package com.example.pioneer_shuttle;import android.content.Intent;import android.os.Bundle;import android.view.MotionEvent;import android.view.View;import android.widget.Button;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;public class Main_Screen extends AppCompatActivity {    Button btnArm;    Button btnMap;    Button btnScl;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.main_screen);        btnArm = (Button)findViewById(R.id.inform);        btnMap = (Button)findViewById(R.id.map);        btnScl = (Button)findViewById(R.id.schedule);        btnArm.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), Alarm.class);                startActivity(intent);                Toast.makeText(getApplicationContext(), "view alarm", Toast.LENGTH_SHORT).show();            }        });        btnMap.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), Map.class);                startActivity(intent);                Toast.makeText(getApplicationContext(), "view map", Toast.LENGTH_SHORT).show();            }        });        btnScl.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), Schedule.class);                startActivity(intent);                Toast.makeText(getApplicationContext(), "view schedule", Toast.LENGTH_SHORT).show();            }        });    }}