package com.example.pioneer_shuttle;import android.app.FragmentManager;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import com.google.android.gms.maps.GoogleMap;import com.google.android.gms.maps.MapFragment;import com.google.android.gms.maps.OnMapReadyCallback;import com.google.android.gms.maps.SupportMapFragment;public class Map extends AppCompatActivity implements OnMapReadyCallback{    private FragmentManager fragmentManager;    private MapFragment mapFragment;    Button btnBack;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.map);        btnBack = (Button)findViewById(R.id.btnBack);        btnBack.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), Main_Screen.class);                startActivity(intent);                Toast.makeText(getApplicationContext(), "Back to Main", Toast.LENGTH_SHORT).show();            }        });        fragmentManager = getFragmentManager();        mapFragment = (MapFragment)fragmentManager.findFragmentById(R.id.map);        mapFragment.getMapAsync(this);    }    @Override    public void onMapReady(GoogleMap googleMap){    }}